mapper.select:

	select no, name, email 
	from user 
	where email=#{email } and password=#{password }


	select name,email,password,gender 
	from user 
	where no=#{no}

mapper.update:

	update user 
	set name=#{name },
	

	password=#{password },
	
	
	 gender=#{gender} 
	where no=#{no}
